mapper.delete:
DELETE FROM


		HOLIDAY_PUBLIC WHERE PUBLIC_DAY LIKE #{date} AND PUBLIC_NUM LIKE #{number}


		HOLIDAY_OFFICE WHERE OFFICE_HOLIDAY LIKE #{date} AND OFFICE_NUM LIKE #{number}

mapper.update:
UPDATE


		HOLIDAY_PUBLIC SET PUBLIC_DAY = #{date},


		HOLIDAY_OFFICE SET OFFICE_HOLIDAY = #{date},


UPT_ID = #{user.USER_ID}, UPT_DT = GETDATE()


		WHERE PUBLIC_NUM LIKE #{number}


		WHERE OFFICE_NUM LIKE #{number}

mapper.select:
SELECT * FROM (SELECT HP.PUBLIC_NUM AS NUMBER, YEAR(HP.PUBLIC_DAY) AS YEAR, 'public' AS TYPE, HP.PUBLIC_DAY AS DATE, DATENAME(WEEKDAY, HP.PUBLIC_DAY) AS WEEKDAY, CONVERT(CHAR(10), HP.REG_DT, 23) AS REG_DT FROM HOLIDAY_PUBLIC HP UNION SELECT HO.OFFICE_NUM AS NUMBER, YEAR(HO.OFFICE_HOLIDAY) AS YEAR, 'office' AS TYPE, HO.OFFICE_HOLIDAY AS DATE, DATENAME(WEEKDAY, HO.OFFICE_HOLIDAY) AS WEEKDAY, CONVERT(CHAR(10), HO.REG_DT, 23) AS REG_DT FROM HOLIDAY_OFFICE HO) UN
AND YEAR(UN.DATE) LIKE #{year}
AND type LIKE #{type}
ORDER BY DATE ASC;
